mapper.select:
select count(*) 
from qna_admin
where qna_no = #{qna_no}

SELECT * 
FROM (
	SELECT ROWNUM RN, A.* 
		FROM (
				SELECT *  
				from qna_admin
				where qna_no = #{qna_no}
				) A
		)
WHERE RN BETWEEN #{start} AND #{end}

select * from qna_admin
where qna_no = #{qna_no}
order by answer_no asc
